"use client";

import React, { useState, useEffect } from 'react';

const TrustedBrands = () => {
  const [isVisible, setIsVisible] = useState(false);
  
  // Brand logos as image URLs instead of SVG
  const brands = [
    {
      name: "Sony",
      logo: "data:image/png;base64," // Placeholder URL for Sony logo
    },
    {
      name: "EA Games",
      logo: "data:image/png;base64," // Placeholder URL for EA Games logo
    },
    {
      name: "Ubisoft",
      logo: "data:image/jpeg;base64," // Placeholder URL for Ubisoft logo
    },
    {
      name: "Activision",
      logo: "data:image/png;base64," // Placeholder URL for Activision logo
    },
    {
      name: "PlayStation",
      logo: "data:image/png;base64," // Placeholder URL for PlayStation logo
    },
    {
      name: "Bandai Namco",
      logo: "data:image/png;base64," // Placeholder URL for Bandai Namco logo
    }
  ];

  // Animation on scroll effect
  useEffect(() => {
    const handleScroll = () => {
      const element = document.getElementById('trusted-brands');
      if (element) {
        const position = element.getBoundingClientRect();
        if (position.top < window.innerHeight - 100) {
          setIsVisible(true);
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Check on initial load
    
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <section 
      id="trusted-brands" 
      className="relative py-16 bg-gradient-to-r from-gray-900 to-blue-900 overflow-hidden"
    >
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        {[...Array(5)].map((_, i) => (
          <div
            key={i}
            className="absolute w-20 h-20 rounded-full bg-blue-500 opacity-5 animate-pulse"
            style={{ 
              left: `${Math.random() * 100}%`, 
              top: `${Math.random() * 100}%`,
              animationDelay: `${i * 0.3}s`,
              animationDuration: `${4 + i}s`
            }}
          />
        ))}
      </div>
      
      {/* Grid pattern overlay */}
      <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-10"></div>

      <div className="container mx-auto px-6 relative z-10">
        <div className={`text-center mb-12 transition-all duration-1000 ${
          isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-12'
        }`}>
          <div className="inline-block bg-gradient-to-r from-blue-600 to-indigo-600 text-sm font-bold px-4 py-1.5 rounded-full mb-6 text-white shadow-lg">
            KOLEKSI GAME
          </div>
          <h2 className="text-3xl md:text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-white via-blue-200 to-blue-300">
            Developer Game yang Tersedia untuk Rental
          </h2>
          <div className="w-24 h-1 bg-blue-600 rounded-full mx-auto mt-4"></div>
          <p className="mt-4 text-gray-300 max-w-2xl mx-auto">
            Kami menyediakan berbagai game dari developer ternama untuk PS3 dan PS4 yang bisa Anda rental dan nikmati
          </p>
        </div>
        
        <div className={`relative mx-auto max-w-6xl transition-all duration-1000 ${
          isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-12'
        }`}>
          {/* Glowing background effect */}
          <div className="absolute inset-0 bg-blue-600 opacity-5 blur-3xl rounded-3xl"></div>
          
          <div className="relative bg-white bg-opacity-10 backdrop-blur-sm rounded-2xl p-8 shadow-2xl border border-blue-500 border-opacity-20">
            <div className="flex flex-wrap justify-around items-center gap-x-8 gap-y-10">
              {brands.map((brand, index) => (
                <div 
                  key={index}
                  className="relative group"
                  style={{ 
                    animationDelay: `${index * 0.2}s`,
                  }}
                >
                  <div className="transform transition-all duration-300 group-hover:scale-110 opacity-80 hover:opacity-100 relative">
                    <img 
                      src={brand.logo} 
                      alt={`${brand.name} logo`} 
                      className="w-28 h-8 object-contain"
                    />
                    <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-0 h-0.5 bg-blue-500 group-hover:w-full transition-all duration-300"></div>
                  </div>
                </div>
              ))}
            </div>
          </div>
          
          {/* Decorative elements */}
          <div className="absolute -top-6 -right-6 w-12 h-12 bg-blue-600 rounded-full opacity-20 animate-pulse"></div>
          <div className="absolute -bottom-4 -left-4 w-8 h-8 bg-indigo-600 rounded-full opacity-20 animate-pulse" style={{animationDelay: "1s"}}></div>
        </div>
        
        {/* Decorative controller icon */}
        <div className="absolute -bottom-8 right-10 text-blue-500 opacity-10 animate-float hidden md:block" style={{animationDuration: "3s"}}>
          <svg xmlns="http://www.w3.org/2000/svg" width="80" height="80" viewBox="0 0 24 24" fill="currentColor" strokeWidth="0">
            <path d="M6 12h4a2 2 0 0 0 2-2V6a2 2 0 0 0-2-2H6a2 2 0 0 0-2 2v4a2 2 0 0 0 2 2Z" />
            <path d="M14 6h4a2 2 0 0 1 2 2v4a2 2 0 0 1-2 2h-4a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2Z" />
            <path d="M6 14h4a2 2 0 0 1 2 2v4a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2v-4a2 2 0 0 1 2-2Z" />
            <path d="M17 20a1 1 0 0 0 0-2v0" />
            <path d="M20 17a1 1 0 0 0-2 0v0" />
          </svg>
        </div>
      </div>
      
      {/* Custom animations */}
      <style jsx global>{`
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        
        .animate-float {
          animation: float 3s ease-in-out infinite;
        }
      `}</style>
    </section>
  );
};

export default TrustedBrands;